function App() {
  const [currentSongIndex, setCurrentSongIndex] = useState(-1);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(new Audio());

  const togglePlay = () => {
    if (currentSongIndex === -1) {
      setCurrentSongIndex(0);
    }
    setIsPlaying(!isPlaying);
  };

  const handlePrev = () => {
    setCurrentSongIndex((currentIndex) =>
      currentIndex === 0 ? song.length - 1 : currentSongIndex - 1
    );
  };

  const handleNext = () => {
    setCurrentSongIndex((currentIndex) => (currentIndex + 1) % song.length);
  };

  useEffect(() => {
    if (currentSongIndex !== -1) {
      const currentSong = song[currentSongIndex];
      audioRef.current.src = currentSong.src;
    }
  }, [currentSongIndex]);

  useEffect(() => {
    if (currentSongIndex !== -1 && isPlaying) {
      audioRef.current.play();
    } else {
      audioRef.current.pause();
    }
  }, [currentSongIndex, isPlaying]);

  const currentSong = song[currentSongIndex];

  return (
    <>
      <h1>Playlist</h1>

      <PlayMusic currentSong={currentSong} song={song} />

      <PLayer
        isPlaying={isPlaying}
        onToggle={togglePlay}
        onPrev={handlePrev}
        onNext={handleNext}
        currentSong={currentSong}
        song={song}
      />
    </>
  );
}

export default App;
